import React,{useState} from "react"
import dateFnsFormat  from "date-fns/format";
import CalendarItem from './CalendarItem'


class NewCalendarItem extends React.Component{

    constructor(props){
        super(props);
        let _newItem={};
            
        _newItem.selectedDay = 0;
        _newItem.subject= "1";
        _newItem.text = "1";
        _newItem.time = "1";
        _newItem.date = "1";
       this.state={newItem:_newItem,somthinghappened:'no',itemKey:1,subject:"",text:"",time:"",date:""};
       this.updateNewItemValue = this.updateNewItemValue.bind(this);       
        this.UpdateComplete = this.UpdateComplete.bind(this);
       this.AddNewItem = this.AddNewItem.bind(this);
       
    }

    AddNewItem(){
        console.log('sending new item:' +this.state.newItem);
        console.log('did something happen?'+this.state.somthinghappened);
        if (this.state.subject.length>0 && this.state.text.length>0 && this.state.time.length>0 && this.state.date.length>0){            
            let _newItem = {};
            let dateFormat ="d"; //display single digit day number
            
            _newItem.selectedDay =  dateFnsFormat(this.state.date,dateFormat);
            _newItem.subject= this.state.subject;
            _newItem.text = this.state.text;
            _newItem.time = this.state.time;
            _newItem.date = this.state.date;
            console.log(_newItem);

            this.props.updateAppt(_newItem);    
            //this.setState({newItem:_newItem,somthinghappened:'yes'});
        }else{
            alert('subject:'+this.state.subject +' text'+this.state.text+' time:'+this.state.time+' date:'+this.state.date);
        }
        
        
    }

    UpdateComplete(_subject, _text, _time, _date){
        if (_subject.length>0 && _text.length>0 && _time.length>0 && _date.length>0){            
            let _newItem = this.state.newItem;
                
            _newItem.selectedDay = 0;
            _newItem.subject= _subject.toString();
            _newItem.text = _text.toString();
            _newItem.time = _time.toString();
            _newItem.date = _date.toString();
            console.log(_newItem);
            
            //this.setState({newItem:_newItem,somthinghappened:'yes'});
        }else{
            alert('subject:'+_subject +' text'+_text+' time:'+_time+' date:'+_date);
        }
        
    }

    render(){
        
    return(
//<CalendarItem saveData={this.AddNewItem} key={this.state.itemKey} updateComplete={this.UpdateComplete.bind(this)}  />
        <div>
            
            <div className="col">
            <div >
            <label >Subject:
                <div>
                <input onChange={this.updateNewItemValue} value={ this.state.subject} type="text" id="NewItemSubject"></input>         
                </div>
            </label>
            </div>
            <div>
                <textarea onChange={this.updateNewItemValue} value={this.state.text} rows="5" id="NewItemText"></textarea>
            </div>
            <div>
                <label htmlFor="NewItemTime">Time:
                <div><input onChange={this.updateNewItemValue} value={this.state.time} type="time" id ="NewItemTime"></input></div></label>
                </div>
            <div>
                <label htmlFor="NewItemDate">Date:
                <div>
                <input onChange={this.updateNewItemValue} value={this.state.date} type="date" id ="NewItemDate"></input></div>
                </label>
            </div>
        </div>
            <button onClick={this.AddNewItem}>Save</button>
        </div>
       );
    }

    updateNewItemValue(evt){
        
        switch(evt.target.id){
            case "NewItemSubject":
                    this.setState({subject:evt.target.value});       
                break;
            case "NewItemText":
                    this.setState({text:evt.target.value});
                    break;
            case "NewItemTime":
                    this.setState({time:evt.target.value});
                break;
            case "NewItemDate":
                    this.setState({date:evt.target.value});
                break;
            default:
                break;
        }
        
    }

}
export default NewCalendarItem;
