import React,{useState} from "react"
import CalendarItem from './CalendarItem'


class NewCalendarItem extends React.Component{

    constructor(props){
        super(props);
        let _newItem={};
            
        _newItem.selectedDay = 0;
        _newItem.subject= "";
        _newItem.text = "";
        _newItem.time = "";
        _newItem.date = "";
        this.state={newItem:_newItem};
    }

    AddNewItem(){
        console.log('sendign new item:' +this.state.newItem.subject);
        this.props.updateAppt(this.state.newItem);
        
    }

    UpdateComplete(_subject, _text, _time, _date){
        if (_subject.length>0 && _text.length>0 && _time.length>0 && _date.length>0){            
            let _newItem={};
                
            _newItem.selectedDay = 0;
            _newItem.subject= _subject;
            _newItem.text = _text;
            _newItem.time = _time;
            _newItem.date = _date;
            console.log(_newItem.subject);
            this.setState={newItem:_newItem};
        }else{
            alert('subject:'+_subject +' text'+_text+' time:'+_time+' date:'+_date);
        }
        
    }

    render(){
        
    return(

        <div>
            <CalendarItem key={Date.now()} updateComplete={this.UpdateComplete.bind(this)}  />
            <button onClick={this.AddNewItem.bind(this)}>Save</button>
        </div>
       );
    }

}
export default NewCalendarItem;
