    }

    buttonClickDelete(){
        //remove the item entry from the state

    }

    buttonClickCancel(){
        //do nothing, close the window
    }


render(){

    if (this.props.isOpen===false)
    return null
  
    let modalStyle = {
      position: 'absolute',
      top: '35%',
      left: '50%',
      transform: 'translate(-50%, -50%)',
      zIndex: '9999',
      width:'25%',
      height:'500px',
      background: '#fff'
      
     
    }

    let btnDelete = <button className="ActionButton" onClick={this.buttonClickDelete}>Delete</button>
    let btnSave = <button className="ActionButton" onClick={this.buttonClickSave}>Save</button>
    let btnCancel = <button className="ActionButton" >Cancel</button>

    return(
    <div className="calendarInputControls calendarEdit">                   
    <span>Update entry</span> 
        <CalendarItem/>
        {btnDelete}
        {btnCancel}
    </div>
    );
}

}
export default EditCalendarItem;
