import React,{useState} from "react"
import CalendarItem from './CalendarItem'
import { isThisSecond } from "date-fns";

class NewCalendarItem extends React.Component{

    constructor(props){
        super(props);
        let _newItem={};
            
        _newItem.selectedDay = 0;
        _newItem.subject= "";
        _newItem.text = "";
        _newItem.time = "";
        _newItem.date = "";
        this.state={newItem:_newItem,
            clearNewItemValues:false};
    }

    AddNewItem(){
        alert('sendign new item');
        this.props.updateAppt(this.state.newItem);
        this.state.clearNewItemValues =true;
    }

    UpdateComplete(_subject, _text, _time, _date){
        if (_subject.length>0 && _text.length>0 && _time.length>0 && _date.length>0){
            // this.setState({subject:_subject,text:_text,time:_time,date:_date});
            let _newItem={};
                
            _newItem.selectedDay = 0;
            _newItem.subject= _subject;
            _newItem.text = _text;
            _newItem.time = _time;
            _newItem.date = _date;
            this.setState={newItem:_newItem};
            
            //alert('subject:'+_subject +' text'+_text+' time:'+_time+' date:'+_date);
        }else{
            alert('subject:'+_subject +' text'+_text+' time:'+_time+' date:'+_date);
        }
        
    }

    render(){
        
    return(

        <div>
            <CalendarItem  updateComplete={this.UpdateComplete.bind(this)} clearValues = {this.state.clearNewItemValues} />
            <button onClick={this.AddNewItem.bind(this)}>Save</button>
        </div>
       );
    }

}
export default NewCalendarItem;
