import React,{useState} from "react"
import CalendarItem from './CalendarItem'
import { isThisSecond } from "date-fns";

class NewCalendarItem extends React.Component{

    AddNewItem(){
        
        this.setState ={
            newItem:{ subject:'',text:'',time:'',date:'',id:''}
        };
    }

    UpdateItem(_subject, _text, _time, _date){
        if (_subject.length>0 && _text.length>0 && _time.length>0 && _date.length>0){
            this.setState({subject:_subject,text:_text,time:_time,date:_date});
            let newItem={};
            
            newItem.selectedDay = 0;
            newItem.subject= "";
            newItem.text = "";
            newItem.time = "";
            newItem.date = "";
            this.props.updateAppt(newItem);
        }else{
            alert('subject:'+_subject +' text'+_text+' time:'+_time+' date:'+_date);
        }
        
    }

    render(){
        
    return(
        <div>
            <CalendarItem updateComplete={this.UpdateItem.bind(this)} itemData="{ this.state.newItem}" />
            <button onClick={this.UpdateItem.bind(this)}>Save</button>
        </div>
       );
    }

}
export default NewCalendarItem;
