import React from "react";


class AppointmentSummary extends React.Component{

    constructor(props){
        super(props)
        this.state={
            appointmentSummaryList:[]
        }
      }

      saveNewCalendarItem(){
          var newItem={};
          newItem.id =this.state.appointmentSummaryList.length+1;
          newItem.selectedDay = 0;
          newItem.subject= "";
          newItem.text = "";
          newItem.time = "";
          newItem.date = "";
          this.state.appointmentSummaryList.push(newItem);
      }

    render(){
        
        let todayAppointments = [];
        this.state.appointmentSummaryList.map(function(appointment){
            if(appointment!=undefined){
                if (appointment.day == this.props.selectedDay){
                    todayAppointments.push(appointment);
                }
            }
            return true;
        });
           

        return(
            <div>
                <span></span>
                <span>{this.props.selectedDay}</span>
                
            </div>
        )

    }

}
export default AppointmentSummary;
