import React from "react";

//Calendar item
class CalendarItem extends React.Component{

constructor(props){
    super(props);
    this.updateNewItemValue = this.updateNewItemValue.bind(this);
    this.state={
        subject:"",text:"",time:"",date:""
    };
}

    updateNewItemValue(evt){
        switch(evt.target.id){
            case "NewItemSubject":
                    this.setState({subject:evt.target.value});
                break;
            case "NewItemText":
                    this.setState({text:evt.target.value});
                break;
            case "NewItemTime":
                    this.setState({time:evt.target.value});
                break;
            case "NewItemDate":
                    this.setState({date:evt.target.value});
                break;
        }
    }

render(){
    return(
        <div className="col">
            <div >
            <label >Subject:
                <div>
                <input onChange={this.updateNewItemValue} value={this.props.itemData.subject} type="text" id="NewItemSubject"></input>         
                </div>
            </label>
            </div>
            <div>
                <textarea onChange={this.updateNewItemValue} value={this.props.itemData.text} rows="5" id="NewItemText"></textarea>
            </div>
            <div>
                <label htmlFor="NewItemTime">Time:
                <div><input onChange={this.updateNewItemValue} value={this.props.itemData.time} type="time" id ="NewItemTime"></input></div></label>
                </div>
            <div>
                <label htmlFor="NewItemDate">Date:
                <div>
                <input onChange={this.updateNewItemValue} value={this.props.itemData.date} type="date" id ="NewItemDate"></input></div>
                </label>
            </div>
        </div>
    )
}

}
export default CalendarItem;
