import React from "react";
import './AppointmentSummary.css';
import EditCalendarItem from "../EditCalendarItem/EditCalendarItem"


class AppointmentSummary extends React.Component{

 
    constructor(props){
        super(props)
        this.state={
            appointmentSummaryList:[] ,
            selectedAppointment:'',
            showEditCalendarItem:false
        }
        this.DisplayAppointments = this.DisplayAppointments.bind(this);
        this.EditSelected = this.EditSelected.bind(this);
        this.SelectCalendarItem = this.SelectCalendarItem.bind(this);
        
      }
      EditSelected(appointment){
          if (typeof appointment != undefined)
            this.setState({showEditCalendarItem:true,selectedAppointment:appointment});
      }

      saveNewCalendarItem(){
          var newItem={};
          newItem.id =this.state.appointmentSummaryList.length+1;
          newItem.selectedDay = 0;
          newItem.subject= "";
          newItem.text = "";
          newItem.time = "";
          newItem.date = "";
          this.state.appointmentSummaryList.push(newItem);
      }

      SelectCalendarItem(appt){
        this.setState({showEditCalendarItem:true,selectedAppointment:appt});
      }
      DisplayAppointments(){

        let appointmentDisplay;
        if (typeof this.state.appointmentSummaryList !=='undefined'&& this.state.appointmentSummaryList.length >0){
          

            appointmentDisplay = <div>
                        
                    
           { this.state.appointmentSummaryList.map(function (appointment){
            if(typeof appointment!==undefined){
             
                if (appointment.day === this.props.selectedDay){ 
                    
                    return (<li>{appointment.subject} : {appointment.text}</li>)

                }
            }
            
        })}
        </div>
        
    }
      }

    render(){
        let todayAppointments;
        let todaySummary = <span> Found {this.props.appointments.length} appointments</span>; 
        let selDay = this.props.selectedDay;      
        //filter the appointments to be displayed by the selected day
        let filteredItems = this.props.appointments.map( function(appt){
            if( selDay== appt.selectedDay){
                return appt;
            }
        });
        //if there are appointments for selected day then sort them by appointment time
        if(typeof filteredItems[0] !== 'undefined'){
            let clickEvent =  this.EditSelected;
            //sort the days appointments
            filteredItems.sort((a, b) => (a.seconds > b.seconds) ? 1 : -1)
            //create the html table to display the apointments in 
            todayAppointments = <div ><table className="appointmentSummary"><thead><tr><th>Subject</th><th>Text</th><th>Time</th><th>Date</th></tr></thead><tbody>
            { 
                filteredItems.map( function(appt){             
                if( selDay== appt.selectedDay){
                         <tr className="summaryTableItem" key={appt.time}><td>{appt.subject}</td><td>{appt.text}</td>
                        <td>{appt.time}</td><td>{appt.date}</td><td > <button onClick={this.SelectCalendarItem.bind(appt)} className="selectToEdit"></button></td></tr>
                    }
                })         
            }
            </tbody>
            </table>
            </div>
        }
           
        return(
            <div >
                <span></span>
                <span>{this.props.selectedDay} :{todaySummary} </span>
                {todayAppointments}
                <EditCalendarItem isOpen={this.state.showEditCalendarItem}></EditCalendarItem>
                
            </div>
        )

    }

}
export default AppointmentSummary;
