}

    render(){

        
        let todayAppointments = <span>asdoasodm</span>;
        let selDay = this.props.selectedDay;      
        console.log('selday:'+selDay);
        console.log(this.props.appointmentList.length);
        
        if ( this.props.appointmentList.length>0)
        {
            console.log('found some appointmnets');
            
            
            //filter the appointmentList to be displayed by the selected day
            let filteredItems = this.props.appointmentList.map( function(appt){
                console.log('selected day:'+selDay +'appointmnet day:'+appt.selectedDay);
                    if( selDay === appt.selectedDay){
                        return appt;
                    }
                });

            //if there are appointmentList for selected day then sort them by appointment time
            if(typeof filteredItems[0] !== 'undefined'){
                console.log('adding items for selected date');
                //sort the days appointmentList
                filteredItems.sort((a, b) => (a.seconds > b.seconds) ? 1 : -1)
                //create the html table to display the apointments in 
                console.log(filteredItems);
                todayAppointments = <div >start<table className="appointmentSummary"><thead><tr><th>Subject</th><th>Text</th><th>Time</th><th>Date</th></tr>
                </thead>
                <tbody>asdas                   
                
                { 
                    filteredItems.map( function(appt){             
                    if( selDay=== appt.selectedDay){
                        console.log('adding item');
                            return <tr className="summaryTableItem" key={appt.time}><td>{appt.subject}rrr</td><td>{appt.text}</td>
                            <td>{appt.time}</td><td>{appt.date}</td><td > <button >Select</button></td></tr>
                        }else{
                            
                        }
                    
                    })         
                }
                </tbody>
                </table>
                end
                </div>
            }
            
        }



        return(
            
           <div> {todayAppointments} </div>
        )
    }
}
export default AppointmentDayItems;
