import React,{useState} from "react"
import CalendarItem from './CalendarItem'
import { isThisSecond } from "date-fns";

class NewCalendarItem extends React.Component{

    AddNewItem(){
        //check the content of the newitem data
        this.props.updateAppt();

        this.state ={
            newItem:{ subject:'',text:'',time:'',date:'',id:''}
        };
    }

    render(){
        
    return(
        <div>
            <CalendarItem itemData={ this.state.newItem} />
            <button onClick={this.AddNewItem.bind(this)}>Save</button>
        </div>
       );
    }

}
export default NewCalendarItem;
