import React from "react";


class AppointmentSummary extends React.Component{

 
    constructor(props){
        super(props)
        this.DisplayAppointments = this.DisplayAppointments.bind(this);
        this.state={
            appointmentSummaryList:[]  
        }
      }

      componentDidUpdate(){
        
        this.DisplayAppointments();   
      }

      saveNewCalendarItem(){
          var newItem={};
          newItem.id =this.state.appointmentSummaryList.length+1;
          newItem.selectedDay = 0;
          newItem.subject= "";
          newItem.text = "";
          newItem.time = "";
          newItem.date = "";
          this.state.appointmentSummaryList.push(newItem);
      }

      DisplayAppointments(){

        let appointmentDisplay;
        if (this.state.appointmentSummaryList!=='undefined'&& this.state.appointmentSummaryList.length >0){
          

            appointmentDisplay = <div>
                        
                    
           { this.state.appointmentSummaryList.map(function (appointment){
            if(appointment!==undefined){
             
                if (appointment.day === this.props.selectedDay){ 
                    
                    return (<li>{appointment.subject} : {appointment.text}</li>)

                }
            }
            
        })}
        </div>
        
    }
      }

    render(){
        
        let todaySummary = <span>{this.props.appointments.length} appointments</span>; 
        let selDay = this.props.selectedDay;
        let filteredItems = this.props.appointments.map( function(appt){
            if( selDay== appt.selectedDay){
                return appt;
            }
        });
        if(filteredItems.length>0){
            filteredItems.map(function(fItem){
                if (fItem!=null){
                    console.log(fItem.seconds);
                }
                
            })
            console.log('filtered:'+filteredItems);
            filteredItems.sort(function(a,b){return a.time<b.time});
            filteredItems.map(function(fItem){
                if (fItem!=null){
                    console.log(fItem.id);
                }
            })
        }
        
        let todayAppointments = <div ><table className="appointmentSummary"><thead><tr><th>Subject</th><th>Text</th><th>Time</th><th>Date</th></tr></thead><tbody>{
        
         this.props.appointments.map( function(appt){
            
            if( selDay== appt.selectedDay){
                return <tr className="summaryTableItem" key={appt.time}><td>{appt.subject}</td><td>{appt.text}</td><td>{appt.time}</td><td>{appt.date}</td><td>{appt.seconds}</td></tr>
            }
         })
         
         }
         </tbody>
         </table>
        </div>
           
           
        return(
            <div >
                <span></span>
                <span>{this.props.selectedDay} :{todaySummary} </span>
                {todayAppointments}
       

                
            </div>
        )

    }

}
export default AppointmentSummary;
